import React from 'react';
import { Text, Button } from '@ui-kitten/components';
import DefaultLayout from '../../components/layouts/DefaultLayout';
import { auth, db } from '../../components/FirebaseAuthenticator';
import { RootState } from '../../app/rootReducer';
import { useSelector } from 'react-redux';

const HomeScreen = () => {
  const userUid = useSelector((state: RootState) => state.setup.userUid);

  const [activePasses, setActivePasses] = React.useState();

  React.useEffect(() => {
    console.log('Fetching Firebase Data: User -> School -> User Passes');
    let unsubscribePasses;
    const unsubscribe = db
      .collection('users')
      .doc(userUid)
      .onSnapshot(doc => {
        const profileData = doc.data();
        unsubscribePasses = profileData.school
          .get()
          .collection('passes')
          .where('issuingUserUid', '==', userUid)
          .onSnapshot(() => {});
        // .then((doc: firebase.firestore.DocumentSnapshot) => {doc.get().collection};
      });

    return () => {
      unsubscribe();
      unsubscribePasses();
    };
  }, []);

  return (
    <DefaultLayout>
      <Text category="h1">{userUid}</Text>
      <Text category="h1">Your Passes</Text>
      <Button onPress={() => auth.signOut()}>Sign out</Button>
    </DefaultLayout>
  );
};

export default HomeScreen;
