import { NavigationContainer } from '@react-navigation/native';
import React from 'react';

import { Provider } from 'react-redux';

import store, { persistor } from './store';

import AppNavigation from '../navigation/AppNavigation';

import { SafeAreaProvider } from 'react-native-safe-area-context';

import FirebaseAuthentication from '../components/FirebaseAuthenticator';
import { StatusBar } from 'react-native';

// UI Kitten
import * as eva from '@eva-design/eva';
import { ApplicationProvider } from '@ui-kitten/components';
import { PersistGate } from 'redux-persist/integration/react';

import { useFonts, Inter_900Black, Inter_400Regular } from '@expo-google-fonts/inter';
import LoadingScreen from '../features/loader/LoadingScreen';

const App = () => {
  let [fontsLoaded] = useFonts({
    Inter_400Regular,
  });

  const strictTheme = {
    'text-font-family': 'OpenSans-Regular',

    'text-heading-1-font-size': 30,
    'text-heading-1-font-weight': '800',
    'text-heading-1-font-family': 'OpenSans-Bold',

    'text-subtitle-1-font-size': 15,
    'text-subtitle-1-font-weight': '600',
    'text-subtitle-1-font-family': 'OpenSans-ExtraBoldItalic',

    'text-paragraph-1-font-size': 12,
    'text-paragraph-1-font-weight': '400',
    'text-paragraph-1-font-family': 'OpenSans-Light',

    'text-caption-1-font-size': 12,
    'text-caption-1-font-weight': '400',
    'text-caption-1-font-family': 'OpenSans-Regular',

    'text-label-font-size': 12,
    'text-label-font-weight': '800',
    'text-label-font-family': 'OpenSans-SemiboldItalic',
  }; // <-- Your Font
  const customMapping = {
    strict: strictTheme,
    components: {
      Button: {
        meta: {
          appearances: {
            outline: {
              default: true, // <-- Set `outline` appearance to be default
            },
          },
        },
      },
    },
  };

  if (!fontsLoaded) {
    return <LoadingScreen />;
  }

  return (
    <Provider store={store}>
      <PersistGate loading={null} persistor={persistor}>
        <ApplicationProvider
          {...eva}
          theme={eva.light}
          mapping={eva.mapping}
          customMapping={customMapping}>
          <FirebaseAuthentication />
          <SafeAreaProvider>
            <NavigationContainer>
              <AppNavigation />
            </NavigationContainer>
          </SafeAreaProvider>
        </ApplicationProvider>
      </PersistGate>
    </Provider>
  );
};

export default App;
